def get_products_by_goal(goal):
    """–ü–æ–ª—É—á–∏—Ç—å –ø—Ä–æ–¥—É–∫—Ç—ã –ø–æ —Ü–µ–ª–∏"""
    return PRODUCTS_DATABASE.get(goal, {})

def get_products_by_category(goal, category):
    """–ü–æ–ª—É—á–∏—Ç—å –ø—Ä–æ–¥—É–∫—Ç—ã –ø–æ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏ –∏ —Ü–µ–ª–∏"""
    goal_products = get_products_by_goal(goal)
    return goal_products.get(category, {})

def search_product(product_name):
    """–ü–æ–∏—Å–∫ –ø—Ä–æ–¥—É–∫—Ç–∞ –ø–æ –Ω–∞–∑–≤–∞–Ω–∏—é –≤–æ –≤—Å–µ—Ö –∫–∞—Ç–µ–≥–æ—Ä–∏—è—Ö"""
    results = {}
    for goal, categories in PRODUCTS_DATABASE.items():
        for category, products in categories.items():
            for name, data in products.items():
                if product_name.lower() in name.lower():
                    if goal not in results:
                        results[goal] = {}
                    if category not in results[goal]:
                        results[goal][category] = {}
                    results[goal][category][name] = data
    return results

def format_product_info(product_name, product_data):
    """–§–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–∏ –æ –ø—Ä–æ–¥—É–∫—Ç–µ"""
    result = f"""üçé **{product_name.title()}** (–Ω–∞ 100–≥)

üìä **–ü–∏—â–µ–≤–∞—è —Ü–µ–Ω–Ω–æ—Å—Ç—å:**
üî• –ö–∞–ª–æ—Ä–∏–∏: **{product_data['–∫–∞–ª–æ—Ä–∏–∏']} –∫–∫–∞–ª**
ü•© –ë–µ–ª–∫–∏: **{product_data['–±–µ–ª–∫–∏']}–≥**
üßà –ñ–∏—Ä—ã: **{product_data['–∂–∏—Ä—ã']}–≥**
üçû –£–≥–ª–µ–≤–æ–¥—ã: **{product_data['—É–≥–ª–µ–≤–æ–¥—ã']}–≥**"""
    
    if '–∫–ª–µ—Ç—á–∞—Ç–∫–∞' in product_data:
        result += f"\nüåæ –ö–ª–µ—Ç—á–∞—Ç–∫–∞: **{product_data['–∫–ª–µ—Ç—á–∞—Ç–∫–∞']}–≥**"
    
    if '–æ–ø–∏—Å–∞–Ω–∏–µ' in product_data:
        result += f"\n\nüí° **–û–ø–∏—Å–∞–Ω–∏–µ:** {product_data['–æ–ø–∏—Å–∞–Ω–∏–µ']}"
    
    return result

def get_recommended_products(goal, category, limit=5):
    """–ü–æ–ª—É—á–∏—Ç—å —Ä–µ–∫–æ–º–µ–Ω–¥—É–µ–º—ã–µ –ø—Ä–æ–¥—É–∫—Ç—ã –¥–ª—è —Ü–µ–ª–∏ –∏ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏"""
    products = get_products_by_category(goal, category)
    if not products:
        return []
    
    # –°–æ—Ä—Ç–∏—Ä—É–µ–º –ø–æ –∫–∞–ª–æ—Ä–∏–π–Ω–æ—Å—Ç–∏ –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç —Ü–µ–ª–∏
    if goal == "–ø–æ—Ö—É–¥–µ–Ω–∏–µ":
        sorted_products = sorted(products.items(), key=lambda x: x[1]['–∫–∞–ª–æ—Ä–∏–∏'])
    elif goal == "–Ω–∞–±–æ—Ä_–º–∞—Å—Å—ã":
        sorted_products = sorted(products.items(), key=lambda x: x[1]['–∫–∞–ª–æ—Ä–∏–∏'], reverse=True)
    else:  # –ø–æ–¥–¥–µ—Ä–∂–∞–Ω–∏–µ
        sorted_products = list(products.items())
    
    return sorted_products[:limit]

